mapper.update:
update qna_comment
set re_step=re_step+1
where ref=#{ref} and re_step >= #{re_step}

mapper.select:
select fullName from qna_attach
where q_idx=#{q_idx}
order by regdate desc

mapper.update:
update qna set subject=#{subject}, contents=#{contents},
passwd=#{passwd}, category=#{category}
where q_idx=#{q_idx}
