mapper.select:
select * FROM rooms, room_photo
where rooms.room_idx = room_photo.room_idx
and rooms.room_idx = #{room_idx}

mapper.update:
update rooms
set admin_confirm='삭제요청'
where room_idx=#{room_idx}

update room_photo set
photo1=#{photo1},
photo2=#{photo2},
photo3=#{photo3},
photo4=#{photo4}
where room_idx=#{room_idx}
